/* globals msg */
msg.payload = msg.payload.toString('hex');
return msg;

/* globals msg, node, util */
var max = 93;
var split = max;

var payload = msg.payload;
var num;
var digit = 1;
for (;; digit++) {
    num = Math.ceil(payload.length / split);
    if (num >= Math.pow(10, digit)) {
        split = split - 2;
    } else {
        break;
    }
}

if (split < 0) {
    node.error("Can't split payload, because payload too long(" + payload.length + ").", msg);
    return null;
}

var zeroPadding = "";
var i;
for (i = 0; i < digit; i++) {
    zeroPadding += "0";
}

var seq;
var amount = (zeroPadding + num).slice(-digit);
var data;
var format;
var msgs = [];
for (i = 0; i < num; i++) {
    seq = (zeroPadding + i).slice(-digit);
    data = payload.slice(split * (i), Math.min(split * (i+1), payload.length));
    format = util.format("@F;%s;%s;%s", seq, amount, data);
    msgs.push({topic: i, payload: format});
}

return [msgs];

/*globals msg, context */
var startTrigger = "@start";
var sendTrigger = "@send";

var keys = [
    "n01", "n02", "n03", "n04", "n05", "n06", "n07", "n08", "n09", "n10",
    "n11", "n12", "n13", "n14", "n15", "n16", "n17", "n18", "n19", "n20",
    "n21", "n22", "n23", "n24", "n25", "n26", "n27", "n28", "n29", "n30",
    "s01", "s02", "s03", "s04", "s05", "s06", "s07", "s08", "s09", "s10",
    "l01",
];

var timestampKey = "gwtimestamp";
var toami;

var payload = msg.payload;
if (msg.payload === startTrigger) {
    context.temp = {};
    return null;
} else if (msg.payload === sendTrigger) {
    if (!context.temp) {
        return null;
    }

    toami = context.temp;
    context.temp = null;

    if (Object.keys(toami).length === 0) {
        return null;
    }

    toami[timestampKey] = Date.now();
    return { payload: toami };
} else {
    if (!context.temp) {
        return null;
    }

    keys.forEach(function(key) {
        if (payload.hasOwnProperty(key)) {
            context.temp[key] = payload[key];
        }
    });
    return null;
}

/*globals msg, context, util */
var payload = msg.payload;
var dir = "/home/pi/toami";

if (Number.isFinite(payload)) {
    // timestamp
    var date = new Date(payload);
    context.filename = util.format("%s/toami_%d%s%s%s%s%s.csv",
        dir,
        date.getFullYear(),
        ("0" + (date.getMonth() + 1)).slice(-2),
        ("0" + date.getDate()).slice(-2),
        ("0" + date.getHours()).slice(-2),
        ("0" + date.getMinutes()).slice(-2),
        ("0" + date.getSeconds()).slice(-2));
    return null;
} else {
    // csv
    return {
        filename: context.filename,
        payload: msg.payload,
    };
}

// $1 : Src address
// $2 : GNRMC
// $3 : Temperature
// $4 : Humidity
// $5 : Atmospheric pressure
var regex = /^([0-9a-fA-F]+),(?:\$GNRMC,(.*),)?BME280,([0-9.]*),([0-9.]*),([0-9.]*)$/;

// $1  : UTC Time
// $2  : A:Valid, V:Invalid
// $3  : Latitude
// $4  : N:North, S:South
// $5  : Longitude
// $6  : E:East, W:West
// $7  : Speed over ground
// $8  : Course over ground
// $9  : UTC Date
// $10 : Magnetic variation
// $11 : Magnetic variation direction (E:East, W:West)
// $12 : Positioning system mode indicator
// $13 : Checksum
var rmc = /^([0-9.]*),(A|V),([0-9.]*),(N|S),([0-9.]*),(E|W),([0-9.]*),([0-9.]*),(\d*),([0-9.]*),(E?|W?),(A|D|E|M|S|N)\*([0-9A-F]{2})$/;

//  $1 : Degree
//  $2 : Minutes
//  $3 : Seconds
var dms = /(\d*)(\d{2})\.(\d*)/;

function translate(value) {
    var degrees = parseInt(value[1], 10);
    var minutes = parseInt(value[2], 10);
    var seconds = parseFloat(value[3].replace(/(\d{2})(\d*)/, "$1.$2"));
    return degrees + (minutes / 60) + (seconds / 3600);
}

/* globals msg, node */
var rssi = msg.rssi;
var payload = msg.payload;

// payload parse
var m = regex.exec(payload);
if (!m) {
    node.error("Invalid msg : " + payload, msg);
    return;
}

var src = parseInt(m[1], 16);
var gnrmc = m[2];
var temperature = parseFloat(m[3]);
var humidity = parseFloat(m[4]);
var pressure = parseFloat(m[5]);

var result = {
    src: src,
    rssi: rssi,
    payload: {
        temperature: temperature,
        humidity: humidity,
        pressure: pressure,
    },
};

if (gnrmc) {
        m = rmc.exec(gnrmc);
    if (!m) {
        node.error("Invalid gnrmc : " + gnrmc, msg);
        return;
    }

    var latitudeValue = m[3];
    var latitudeDirection = m[4];

    var longitudeValue = m[5];
    var longitudeDirection = m[6];

    m = dms.exec(latitudeValue);
    if (!m) {
        node.error("Invalid latitude : " + latitudeValue , msg);
        return;
    }
    var latitude = translate(m);

    m = dms.exec(longitudeValue);
    if (!m) {
        node.error("Invalid longitude : " + longitudeValue , msg);
        return;
    }
    var longitude = translate(m);

    if (latitudeDirection === "S") {
        latitude *= -1;
    }

    if (longitudeDirection === "W") {
        longitude *= -1;
    }

    result.payload.location = { latitude: latitude, longitude: longitude };
}

return result;

